import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {

  selectedFile!: File;
  selectedLanguage: string = 'English';
  question: string = '';
  answer: string = '';
  languages: string[] = [
    'English', 'Hindi', 'Tamil', 'Malayalam', 'Telugu', 'French', 'Spanish', 'German'
  ];

  private BASE_URL = 'http://localhost:5000';

  constructor(private http: HttpClient) { }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) {
      alert('Please select a file!');
      return;
    }

    const formData = new FormData();
    formData.append('file', this.selectedFile);
    formData.append('language', this.selectedLanguage);

    this.http.post(`${this.BASE_URL}/upload`, formData)
      .subscribe({
        next: (res: any) => { 
          alert(res.message); 
          this.answer = ''; 
        },
        error: (err) => { 
          console.error(err); 
          alert('File upload failed!'); 
        }
      });
  }

  askQuestion() {
    if (!this.question) {
      alert('Please type a question!');
      return;
    }

    const formData = new FormData();
    formData.append('question', this.question);

    this.http.post(`${this.BASE_URL}/ask`, formData)
      .subscribe({
        next: (res: any) => { this.answer = res.answer; },
        error: (err) => { 
          console.error(err); 
          alert('Failed to get answer!'); 
        }
      });
  }
}

<div class="container">
  <h2>Upload File</h2>
  <input type="file" (change)="onFileSelected($event)">

  <label for="language">Select Language:</label>
  <select [(ngModel)]="selectedLanguage">
    <option *ngFor="let lang of languages" [value]="lang">{{ lang }}</option>
  </select>

  <button (click)="uploadFile()">Upload</button>

  <hr>

  <h2>Ask Question</h2>
  <input type="text" [(ngModel)]="question" placeholder="Type your question">
  <button (click)="askQuestion()">Ask</button>

  <div *ngIf="answer">
    <h3>Answer:</h3>
    <p>{{ answer }}</p>
  </div>
</div>
