import { Component } from '@angular/core';
import { FileService } from '../file.service';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {

  selectedFile!: File;
  selectedLanguage: string = 'English';
  question: string = '';
  answer: string = '';
  languages: string[] = ['English', 'Hindi', 'Tamil', 'Malayalam', 'Telugu', 'French', 'Spanish', 'German'];

  constructor(private fileService: FileService) { }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) { alert("Please select a file!"); return; }

    this.fileService.uploadFile(this.selectedFile, this.selectedLanguage)
      .subscribe({
        next: (res) => { alert(res.message); this.answer = ''; },
        error: (err) => { console.error(err); alert('File upload failed!'); }
      });
  }

  askQuestion() {
    if (!this.question) { alert("Please type a question!"); return; }

    this.fileService.askQuestion(this.question)
      .subscribe({
        next: (res) => { this.answer = res.answer; },
        error: (err) => { console.error(err); alert('Failed to get answer!'); }
      });
  }
}
