import { Component } from '@angular/core';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  uploadMessage: string = '';
  question: string = '';
  response: string = '';

  onFileSelected(event: any): void {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
      this.uploadMessage = '';
      this.response = ''; // Reset the answer on new file selection
      this.question = ''; // Optionally clear question as well
    }
  }

  onUpload(): void {
    if (this.selectedFile) {
      // Replace this logic with actual file upload if needed
      this.uploadMessage = `File uploaded: "${this.selectedFile.name}"`;
    }
  }

  onAsk(): void {
    if (this.question && this.selectedFile) {
      // Replace this logic with your real AI answer flow
      this.response = `You asked: "${this.question}" about "${this.selectedFile.name}"`;
    }
  }
}
