import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  question: string = '';
  answer: string = '';

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post<any>('http://localhost:5000/upload', formData).subscribe(
      res => alert('‚úÖ File uploaded! Now ask a question.'),
      err => alert('‚ùå Error: ' + err.error?.error)
    );
  }

  askQuestion() {
    if (!this.question) return;

    this.http.post<any>('http://localhost:5000/ask', { question: this.question }).subscribe(
      res => this.answer = res.answer,
      err => alert('‚ùå Error getting answer: ' + err.error?.error)
    );
  }
}
<div class="container">
  <h2>üìÑ File Reader Q&A</h2>

  <!-- File Upload -->
  <input type="file" (change)="onFileSelected($event)" accept=".pdf,.png,.jpg,.jpeg" />
  <button (click)="uploadFile()" [disabled]="!selectedFile">Upload</button>

  <br /><br />

  <!-- Ask Question -->
  <input type="text" [(ngModel)]="question" placeholder="Ask something about the file..." />
  <button (click)="askQuestion()" [disabled]="!question">Ask</button>

  <div *ngIf="answer">
    <h3>üß† Answer:</h3>
    <p>{{ answer }}</p>
  </div>
</div>
