import { Component } from '@angular/core';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  notificationMessage: string = '';
  notificationType: string = ''; // e.g. 'success', 'error', 'info'
  question: string = '';
  response: string = '';

  onFileSelected(event: any): void {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
      this.notificationMessage = '';
      this.notificationType = '';
      this.response = '';
      this.question = '';
    }
  }

  onUpload(): void {
    if (this.selectedFile) {
      // Simulate successful upload (for real use, set after successful upload API response)
      this.notificationMessage = 'Uploaded successfully! Now ask a question';
      this.notificationType = 'success';
    } else {
      this.notificationMessage = 'No file selected.';
      this.notificationType = 'error';
    }
  }

  onAsk(): void {
    if (!this.selectedFile) {
      this.notificationMessage = "No file uploaded.";
      this.notificationType = "error";
      return;
    }
    if (!this.question) {
      this.notificationMessage = "Please enter a question.";
      this.notificationType = 'error';
      return;
    }
    // Replace with actual AI call
    this.notificationMessage = '';
    this.response = `You asked: ${this.question}`;
  }
}
<!-- Notification Box (at very top of page) -->
<div *ngIf="notificationMessage" [ngClass]="notificationType" class="notification-box">
  {{ notificationMessage }}
  <button class="close-btn" (click)="notificationMessage=''">Okay</button>
</div>

<h1 class="main-title">AI File Reader</h1>
<div class="file-reader-card">
  <h2 class="card-title">Upload a PDF or Image</h2>
  <div class="centered-row">
    <input type="file" (change)="onFileSelected($event)" />
  </div>
  <div class="centered-row">
    <button (click)="onUpload()" [disabled]="!selectedFile" class="blue-btn">Upload</button>
  </div>

  <h3 class="question-title">Ask a question about the file:</h3>
  <div class="centered-row">
    <input
      type="text"
      class="question-input"
      [(ngModel)]="question"
      [disabled]="!selectedFile"
      placeholder="Type your question here" />
  </div>
  <div class="centered-row">
    <button (click)="onAsk()" [disabled]="!question || !selectedFile" class="blue-btn">Ask</button>
  </div>
  <div *ngIf="response" class="answer-box">
    <span class="answer-label">Answer:</span>
    <p>{{ response }}</p>
  </div>
</div>
