import { Component } from '@angular/core';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  file: File | null = null;
  message: string = '';
  answer: string = '';

  onFileSelected(event: any) {
    this.file = event.target.files[0];
  }

  uploadFile() {
    if (this.file) {
      this.message = "‚úÖ File uploaded successfully! You can now ask a question.";
    } else {
      this.message = "‚ö†Ô∏è Please select a file first.";
    }
  }

  askQuestion() {
    if (this.file) {
      this.answer = "ü§ñ This is a sample AI answer for your uploaded file.";
    } else {
      this.answer = "‚ö†Ô∏è Please upload a file first.";
    }
  }
}

<div class="file-reader-container">
  <h2>üìÇ Upload a PDF or Image</h2>

  <!-- File Input -->
  <input type="file" (change)="onFileSelected($event)">
  <button (click)="uploadFile()" [disabled]="!file">Upload</button>

  <!-- Upload Status -->
  <div *ngIf="message" class="message">{{ message }}</div>

  <!-- Question Box -->
  <div *ngIf="file" class="question-section">
    <h3>‚ùì Ask a question about the file:</h3>
    <input type="text" #questionInput placeholder="Type your question here...">
    <button (click)="askQuestion()">Ask</button>
  </div>

  <!-- Answer -->
  <div *ngIf="answer" class="answer-box">
    <h3>‚úÖ Answer:</h3>
    <p>{{ answer }}</p>
  </div>
</div>
