import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  question: string = '';
  answer: string = '';
  fileUploaded: boolean = false;

  private apiUrl = 'http://127.0.0.1:5000'; // Flask backend

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0] ?? null;
  }

  uploadFile() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post(`${this.apiUrl}/upload`, formData).subscribe({
      next: () => {
        this.fileUploaded = true;
      },
      error: (err) => {
        console.error('Upload failed:', err);
      }
    });
  }

  onAsk() {
    if (!this.question) return;

    this.http.post<{ answer: string }>(`${this.apiUrl}/ask`, { question: this.question }).subscribe({
      next: (res) => {
        this.answer = res.answer;
      },
      error: (err) => {
        console.error('Ask failed:', err);
      }
    });
  }
}

<div class="file-reader-container">
  <h2>Upload a PDF or Image</h2>

  <!-- Upload Section -->
  <input type="file" (change)="onFileSelected($event)">
  <button (click)="uploadFile()" [disabled]="!selectedFile || uploading">
    {{ uploading ? 'Uploading...' : 'Upload' }}
  </button>

  <!-- Question & Answer Section -->
  <div *ngIf="fileUploaded" class="qa-section">
    <h3>Ask a question about the file:</h3>
    <input type="text" [(ngModel)]="question" placeholder="Type your question here...">
    <button (click)="onAsk()" [disabled]="!question || loadingAnswer">
      {{ loadingAnswer ? 'Thinking...' : 'Ask' }}
    </button>

    <div *ngIf="answer" class="answer-box">
      <h4>Answer:</h4>
      <p>{{ answer }}</p>
    </div>
  </div>
</div>
