import { Component } from '@angular/core';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  question: string = '';
  response: string = '';

  onFileSelected(event: any): void {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
    }
  }

  onUpload(): void {
    // Placeholder: Add your file upload logic here!
    this.response = 'File uploaded: ' + (this.selectedFile ? this.selectedFile.name : 'No file');
  }

  onAsk(): void {
    // Placeholder: Add your file question/answer logic here!
    this.response = 'You asked: "' + this.question + '" about "' + (this.selectedFile ? this.selectedFile.name : 'No file') + '"';
  }
}

<div class="file-reader-wrapper">
  <h1 class="main-title">AI File Reader</h1>
  <div class="file-reader-card">
    <h2 class="card-title">Upload a PDF or Image</h2>
    <div class="centered-row">
      <input type="file" (change)="onFileSelected($event)" />
    </div>
    <div class="centered-row">
      <button (click)="onUpload()" [disabled]="!selectedFile" class="blue-btn">Upload</button>
    </div>
    <h3 class="question-title">Ask a question about the file:</h3>
    <div class="centered-row">
      <input type="text" class="question-input" [(ngModel)]="question" />
    </div>
    <div class="centered-row">
      <button (click)="onAsk()" [disabled]="!question" class="blue-btn">Ask</button>
    </div>
    <div *ngIf="response" class="answer-box">
      <span class="answer-label">Answer:</span>
      <p>{{ response }}</p>
    </div>
  </div>
</div>
