import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  uploadedText: string = '';
  response: string = '';
  question: string = '';
  showModal: boolean = false;

  private apiUrl = 'http://localhost:5000'; // Update if your backend URL is different

  constructor(private http: HttpClient) {}

  // Handle file selection
  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;
      this.uploadedText = '';
      this.response = '';
      this.question = '';
      this.showModal = false;
    }
  }

  // Upload the selected file
  onUpload() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post<{ text_preview?: string }>(`${this.apiUrl}/upload`, formData).subscribe({
      next: (res) => {
        this.uploadedText = res.text_preview || '';
        this.showModal = true; // Show modal alert
      },
      error: (err: any) => {
        console.error('Upload error:', err);
        alert('Failed to upload file.');
      }
    });
  }

  // Ask a question about the uploaded file
  onAsk() {
    if (!this.question || !this.uploadedText) {
      alert('Please enter a question and upload a file first.');
      return;
    }

    this.http.post<{ answer?: string }>(`${this.apiUrl}/ask`, {
      question: this.question
    }).subscribe({
      next: (res) => {
        this.response = res.answer || 'No answer returned.';
      },
      error: (err: any) => {
        console.error('Backend error:', err);
        this.response = 'Could not get answer from backend.';
      }
    });
  }

  // Close modal
  onModalOk() {
    this.showModal = false;
  }
}

<div class="container">
  <h1 class="title">{{ title }}</h1>

  <!-- Status Message -->
  <div *ngIf="apiStatus" class="status-message">
    {{ apiStatus }}
  </div>

  <!-- Language Selector -->
  <div class="language-selector">
    <select [(ngModel)]="selectedLanguage">
      <option *ngFor="let lang of languages" [value]="lang">{{ lang | titlecase }}</option>
    </select>
    <button (click)="setLanguage()" [disabled]="isLoading">
      {{ isLoading ? 'Setting...' : 'Set Language' }}
    </button>
  </div>

  <!-- File Upload -->
  <div class="upload-section">
    <input 
      type="file" 
      (change)="onFileSelected($event)" 
      accept=".pdf,.jpg,.jpeg,.png" 
      [disabled]="isLoading"
    />
    <button (click)="uploadFile()" [disabled]="!selectedFile || isLoading">
      {{ isLoading ? 'Uploading...' : 'Upload' }}
    </button>

    <!-- File Preview Card -->
    <div *ngIf="selectedFile" class="file-preview">
      📄 {{ selectedFile.name }}
      <button class="remove-btn" (click)="removeFile()" [disabled]="isLoading">❌</button>
    </div>
  </div>

  <!-- Question Box -->
  <div class="ask-section">
    <input
      type="text"
      [(ngModel)]="question"
      placeholder="Ask your question here"
      (keyup.enter)="askQuestion()"
      [disabled]="isLoading"
    />
    <button (click)="askQuestion()" [disabled]="!question || isLoading">
      {{ isLoading ? 'Thinking...' : 'Ask' }}
    </button>
  </div>

  <!-- Answer Display -->
  <div class="answer-section" *ngIf="answer">
    <h3>Answer ({{ selectedLanguage | titlecase }}):</h3>
    <div class="answer-box">
      {{ answer }}
    </div>
  </div>

  <!-- Reset Button -->
  <button class="reset-btn" (click)="resetForm()">Reset All</button>
</div>
