import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  showModal = false;
  question = '';
  response = '';
  ocrText = '';

  constructor(private http: HttpClient) {}

  onFileSelected(event: any): void {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
      this.response = '';
      this.question = '';
    }
  }

  onUpload(): void {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);

      this.http.post<{ message: string; text?: string }>(
        'http://localhost:5000/upload',
        formData
      ).subscribe({
        next: (res) => {
          this.showModal = true;
          this.ocrText = res.text || '';
        },
        error: (err) => {
          console.error('Upload error:', err);
          this.response = 'Upload failed.';
        }
      });
    }
  }

  onModalOk() {
    this.showModal = false;
  }

  onAsk(): void {
    if (!this.question || !this.selectedFile) return;

    this.http.post<{ answer: string }>(
      'http://localhost:5000/ask',
      { question: this.question }
    ).subscribe({
      next: (res) => {
        this.response = res.answer;
      },
      error: (err) => {
        console.error('Backend error:', err);
        this.response = 'Could not get answer from the AI backend.';
      }
    });
  }
}

<div class="file-reader-container">
  <h2>📂 Upload a PDF or Image</h2>

  <!-- File Input -->
  <input type="file" (change)="onFileSelected($event)">
  <button (click)="uploadFile()" [disabled]="!file">Upload</button>

  <!-- Upload Status -->
  <div *ngIf="message" class="message">{{ message }}</div>

  <!-- Question Box -->
  <div *ngIf="file" class="question-section">
    <h3>❓ Ask a question about the file:</h3>
    <input type="text" #questionInput placeholder="Type your question here...">
    <button (click)="askQuestion()">Ask</button>
  </div>

  <!-- Answer -->
  <div *ngIf="answer" class="answer-box">
    <h3>✅ Answer:</h3>
    <p>{{ answer }}</p>
  </div>
</div>
