uploadMessage: string = '';
response: string = '';
selectedFile: File | null = null;
question: string = '';

onFileSelected(event: any) {
  this.selectedFile = event.target.files[0];
  this.uploadMessage = '';
}

onUpload() {
  // Your upload logic here
  if (this.selectedFile) {
    this.uploadMessage = 'File uploaded: "' + this.selectedFile.name + '"';
  }
}

onAsk() {
  // Your main question/answer logic
  this.response = `You asked: "${this.question}" about "${this.selectedFile ? this.selectedFile.name : ''}"`;
}
