import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  question: string = '';
  answer: string = '';

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post<any>('http://localhost:5000/upload', formData).subscribe(
      res => alert('✅ File uploaded! Now ask a question.'),
      err => alert('❌ Error: ' + err.error?.error)
    );
  }

  askQuestion() {
    if (!this.question) return;

    const formData = new FormData();
    // Send the file along with the question every time
    if (this.selectedFile) {
      formData.append('file', this.selectedFile);
    }
    formData.append('question', this.question);

    this.http.post<any>('http://localhost:5000/ask', formData).subscribe(
      res => this.answer = res.answer,
      err => alert('❌ Error getting answer: ' + err.error?.error)
    );
  }
}
<div class="file-reader-container">
  <h2>Upload a PDF or Image</h2>

  <input type="file" (change)="onFileSelected($event)">
  <button (click)="uploadFile()" [disabled]="!selectedFile">Upload</button>

  <div *ngIf="selectedFile" class="question-section">
    <h3>Ask a question about the file:</h3>
    <input type="text" [(ngModel)]="question" placeholder="Type your question here..." />
    <button (click)="askQuestion()" [disabled]="!question">Ask</button>
  </div>

  <div *ngIf="answer" class="answer-box">
    <h4>Answer:</h4>
    <p>{{ answer }}</p>
  </div>
</div>
