import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  uploadedText: string = '';
  response: string = '';
  question: string = '';
  showModal: boolean = false;

  private apiUrl = 'http://localhost:5000'; // Update if your backend URL is different

  constructor(private http: HttpClient) {}

  // Handle file selection
  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;
      this.uploadedText = '';
      this.response = '';
      this.question = '';
      this.showModal = false;
    }
  }

  // ✅ Remove selected file
  removeFile() {
    this.selectedFile = null;
    this.uploadedText = '';
    this.response = '';
    this.question = '';
    this.showModal = false;
  }

  // Upload the selected file
  onUpload() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post<{ text_preview?: string }>(`${this.apiUrl}/upload`, formData).subscribe({
      next: (res) => {
        this.uploadedText = res.text_preview || '';
        this.showModal = true; // Show modal alert
      },
      error: (err: any) => {
        console.error('Upload error:', err);
        alert('Failed to upload file.');
      }
    });
  }

  // Ask a question about the uploaded file
  onAsk() {
    if (!this.question || !this.uploadedText) {
      alert('Please enter a question and upload a file first.');
      return;
    }

    this.http.post<{ answer?: string }>(`${this.apiUrl}/ask`, {
      question: this.question
    }).subscribe({
      next: (res) => {
        this.response = res.answer || 'No answer returned.';
      },
      error: (err: any) => {
        console.error('Backend error:', err);
        this.response = 'Could not get answer from backend.';
      }
    });
  }

  // Close modal
  onModalOk() {
    this.showModal = false;
  }
}

<!-- Modal -->
<div *ngIf="showModal" class="modal-backdrop">
  <div class="custom-modal">
    <div class="modal-message">
      <span style="font-size:2rem; margin-inline-end:7px;">📤</span>
      File uploaded successfully!<br>
      <span style="font-size:1.19rem;">You can now ask a question.</span>
    </div>
    <button class="modal-btn" (click)="onModalOk()">OK</button>
  </div>
</div>

<h1 class="main-title">AI File Reader</h1>

<div class="file-reader-card">
  <h2 class="card-title">Upload a PDF or Image</h2>

  <div class="centered-row">
    <input type="file" (change)="onFileSelected($event)" />
  </div>
  <div class="centered-row">
    <button (click)="onUpload()" [disabled]="!selectedFile" class="blue-btn">Upload</button>
  </div>

  <h3 class="question-title">Ask a question about the file:</h3>
  <div class="centered-row">
    <input
      type="text"
      class="question-input"
      [(ngModel)]="question"
      [disabled]="!uploadedText"
      placeholder="Type your question here" />
  </div>
  <div class="centered-row">
    <button (click)="onAsk()" [disabled]="!question || !uploadedText" class="blue-btn">Ask</button>
  </div>

  <div *ngIf="response" class="answer-box">
    <span class="answer-label">Answer:</span>
    <div class="answer-text">{{ response }}</div>
  </div>
</div>
