import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {

  selectedFile!: File;
  selectedLanguage: string = 'English';
  question: string = '';
  answer: string = '';
  languages: string[] = [
    'English', 'Hindi', 'Tamil', 'Malayalam', 'Telugu', 'French', 'Spanish', 'German'
  ];

  private BASE_URL = 'http://localhost:5000';

  constructor(private http: HttpClient) { }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) {
      alert('Please select a file!');
      return;
    }

    const formData = new FormData();
    formData.append('file', this.selectedFile);
    formData.append('language', this.selectedLanguage);

    this.http.post(`${this.BASE_URL}/upload`, formData)
      .subscribe({
        next: (res: any) => { 
          alert(res.message); 
          this.answer = ''; 
        },
        error: (err) => { 
          console.error(err); 
          alert('File upload failed!'); 
        }
      });
  }

  askQuestion() {
    if (!this.question) {
      alert('Please type a question!');
      return;
    }

    const formData = new FormData();
    formData.append('question', this.question);

    this.http.post(`${this.BASE_URL}/ask`, formData)
      .subscribe({
        next: (res: any) => { this.answer = res.answer; },
        error: (err) => { 
          console.error(err); 
          alert('Failed to get answer!'); 
        }
      });
  }
}

<div class="file-reader-container">
  <h2>AI File Reader</h2>
  <div class="form-group">
    <input type="file" (change)="onFileSelected($event)" />
    <button [disabled]="!selectedFile" (click)="uploadFile()">Upload</button>
  </div>

  <div class="form-group">
    <input
      type="text"
      [(ngModel)]="question"
      placeholder="Ask a question..."
    />
    <button [disabled]="!question" (click)="askQuestion()">Ask</button>
  </div>

  <div *ngIf="answer" class="response-box">
    {{ answer }}
  </div>
</div>
