import { Component } from '@angular/core';

@Component({
  selector: 'app-file-reader',
  templateUrl: './file-reader.component.html',
  styleUrls: ['./file-reader.component.css']
})
export class FileReaderComponent {
  selectedFile: File | null = null;
  question: string = '';
  response: string = '';

  onFileSelected(event: any): void {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
    }
  }

  onUpload(): void {
    // Placeholder: Add your file upload logic here!
    this.response = 'File uploaded: ' + (this.selectedFile ? this.selectedFile.name : 'No file');
  }

  onAsk(): void {
    // Placeholder: Add your file question/answer logic here!
    this.response = 'You asked: "' + this.question + '" about "' + (this.selectedFile ? this.selectedFile.name : 'No file') + '"';
  }
}

<div style="background: #f8f8f8; margin: 50px auto; width: 60%; border-radius: 20px; box-shadow: 0 0 10px #d8d8d8; padding: 40px 0;">
  <h1 style="text-align:center; font-family: Times New Roman, serif; font-weight: bold; margin-bottom: 24px;">
    Upload a PDF or Image
  </h1>
  <div style="text-align:center; margin-bottom: 25px;">
    <input type="file" (change)="onFileSelected($event)" />
  </div>
  <div style="text-align:center; margin-bottom: 32px;">
    <button (click)="onUpload()" [disabled]="!selectedFile" 
      style="background:#028cfc; border:none; color:white; padding:10px 30px; border-radius:7px; font-size:17px; cursor:pointer;">
      Upload
    </button>
  </div>
  <h2 style="text-align:center; font-family: Times New Roman, serif; font-weight: bold; margin-bottom: 20px;">
    Ask a question about the file:
  </h2>
  <div style="text-align: center;">
    <input style="width: 45%; padding: 8px; font-size: 16px;" type="text" [(ngModel)]="question" />
  </div>
  <div style="text-align: center; margin:20px 0;">
    <button (click)="onAsk()" [disabled]="!question"
      style="background:#028cfc; border:none; color:white; padding:8px 26px; border-radius:7px; font-size:17px; cursor:pointer;">
      Ask
    </button>
  </div>
  <div *ngIf="response" style="margin: 0 auto; background: #eaffea; border-left: 6px solid #27a327; border-radius: 9px; width: 90%; padding: 18px 16px; font-size: 1.12rem;">
    <span style="font-family: Times New Roman, serif; font-weight:bold;">Answer:</span>
    <p style="margin-top: 10px;">{{ response }}</p>
  </div>
</div>
