import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-chat',
  templateUrl: './file-chat.component.html',
  styleUrls: ['./file-chat.component.css']
})
export class FileChatComponent {
  selectedFile: File | null = null;
  question: string = '';
  response: string | null = null;

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  uploadFile() {
    if (!this.selectedFile) return;

    const formData = new FormData();
    formData.append('file', this.selectedFile);

    this.http.post('http://localhost:5000/upload', formData).subscribe({
      next: () => {
        alert('File uploaded successfully! You can now ask a question.');
      },
      error: () => {
        alert('File upload failed.');
      }
    });
  }

